package com.ghosttrio.judomanager.batch.feign;import com.ghosttrio.judomanager.batch.feign.model.response.User;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import java.util.List;@FeignClient(        name = "LoadUserClient", url = "http://localhost:8000/api/v1/users/today")public interface LoadUserClient {    @GetMapping    List<User> findTodaySignupUser();}